-- Script to re-create SQLite database for the book 
-- "A Practical Introduction to Databases"

-- example from introduction

CREATE TABLE fruit_stand
(
    item  TEXT,
    price NUMERIC,
    unit  TEXT
);


-- simple books dataset

CREATE TABLE simple_authors
(
    name  TEXT,
    birth DATE,
    death DATE
);


CREATE TABLE simple_books
(
    title            TEXT,
    author           TEXT,
    publication_year INTEGER,
    genre            TEXT
);


-- books dataset

CREATE TABLE authors
(
    author_id INTEGER PRIMARY KEY,
    name      TEXT NOT NULL,
    birth     DATE,
    death     DATE
);


CREATE TABLE books
(
    book_id          INTEGER PRIMARY KEY,
    author_id        INTEGER NOT NULL REFERENCES authors (author_id),
    title            TEXT NOT NULL,
    publication_year INTEGER
);


CREATE TABLE awards
(
    award_id INTEGER PRIMARY KEY,
    name     TEXT NOT NULL,
    sponsor  TEXT,
    criteria TEXT
);


CREATE TABLE authors_awards
(
    author_id INTEGER NOT NULL REFERENCES authors (author_id),
    award_id  INTEGER NOT NULL REFERENCES awards (award_id),
    year      INTEGER,
    PRIMARY KEY (author_id, award_id)
);


CREATE TABLE books_awards
(
    book_id  INTEGER NOT NULL REFERENCES books (book_id),
    award_id INTEGER NOT NULL REFERENCES awards (award_id),
    year     INTEGER,
    PRIMARY KEY (book_id, award_id)
);


CREATE TABLE editions
(
    edition_id         INTEGER PRIMARY KEY,
    book_id            INTEGER NOT NULL REFERENCES books (book_id),
    publication_year   INTEGER,
    publisher          TEXT,
    publisher_location TEXT,
    title              TEXT,
    pages              INTEGER,
    isbn10             TEXT,
    isbn13             TEXT
);


-- bookstore dataset

CREATE TABLE bookstore_inventory
(
    stock_number INTEGER PRIMARY KEY AUTOINCREMENT,
    author       TEXT,
    title        TEXT,
    condition    TEXT,
    price        NUMERIC
);


CREATE TABLE bookstore_sales
(
    receipt_number INTEGER PRIMARY KEY AUTOINCREMENT,
    stock_number   INTEGER,
    date_sold      DATE DEFAULT CURRENT_DATE,
    payment        TEXT
);


